mapper.insert:

    SELECT subject_seq.NEXTVAL FROM dual

    INSERT INTO subject(
    id, name, credit, description, professor_id, dept_id
)
    VALUES(
    #{id}, #{name}, #{credit}, #{description}, #{professorId}, #{deptId}
)

mapper.update:
UPDATE subject

    name = #{name},
    credit = #{credit},
    description = #{description},
    dept_id = #{deptId}

WHERE id = #{id}
