mapper.select:
SELECT id, username, password_temp, email, phone, name, role
FROM users
WHERE id = #{id}

SELECT id, username, password_temp, email, phone, name, role
FROM users
WHERE role = #{role}

SELECT id, username, password_temp, email, phone, name, role
FROM users
WHERE username = #{username}

mapper.update:
UPDATE users
SET username = #{username}
WHERE id = #{id}

UPDATE users
SET password = #{password},
    password_temp = #{passwordTemp}
WHERE username = #{username}

mapper.select:
SELECT *
FROM users
WHERE username = #{username}
  AND name = #{name}
  AND email = #{email, jdbcType=VARCHAR}
